import org.apache.spark._
import org.apache.spark.graphx._
import org.apache.spark.rdd.RDD

val graph = GraphLoader.edgeListFile(sc, "s3://hadoopspark/hw13/in/all-edges-out.txt")
val ranks = graph.staticPageRank(10).vertices
val articles = sc.textFile("s3://ucb-mids-mls-networks/wikipedia/indices.txt").map { line =>
  val fields = line.split("\t")
  (fields(1).toInt, fields(0))
}
val ranksByArticle = articles.join(ranks).map {
  case (id, (article, rank)) => (article, rank)
}
ranksByArticle.sortBy(_._2,false).take(20).foreach{ println }
